            task.wait(2)
            pcall(function()
                TeleportService:Teleport(game.PlaceId, LocalPlayer)
            end)
        else
            
            stopFlyMode()
            notify(NHcRhKFyXQwqikhYQaZhYZtJVUsVBSAtLocEDbbdtpEaOtdvwY({'*******************************************************************','***************************************************************************************************************','*************************************************************************************************************','**************************************************************************************************','***************************************************************************************************************'}),NHcRhKFyXQwqikhYQaZhYZtJVUsVBSAtLocEDbbdtpEaOtdvwY({'***********************************************************************************','********************************************************************************************************************','***************************************************************************************************************','****************************************************************************************************************','****************************************************************************************************************','*****************************************************************************************************','****************************************************************************************************'}),2)
